import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { XMarkIcon } from '@heroicons/react/24/solid';
import { createCharacter } from '../services/api';

interface AddCharacterModalProps {
    isOpen: boolean;
    onClose: () => void;
    onCharacterAdded: () => void;
}

const AddCharacterModal: React.FC<AddCharacterModalProps> = ({ isOpen, onClose, onCharacterAdded }) => {
    const [name, setName] = useState('');
    const [movie, setMovie] = useState('');
    const [chatStyle, setChatStyle] = useState('');
    const [exampleResponse, setExampleResponse] = useState('');
    const [exampleResponses, setExampleResponses] = useState<string[]>([]);
    const [error, setError] = useState('');
    const [isSubmitting, setIsSubmitting] = useState(false);

    const handleAddExample = () => {
        if (exampleResponse.trim()) {
            setExampleResponses([...exampleResponses, exampleResponse.trim()]);
            setExampleResponse('');
        }
    };

    const handleRemoveExample = (index: number) => {
        setExampleResponses(exampleResponses.filter((_, i) => i !== index));
    };

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();
        setError('');
        setIsSubmitting(true);

        try {
            if (exampleResponses.length === 0) {
                throw new Error('Please add at least one example response');
            }

            await createCharacter({
                name,
                movie,
                chat_style: chatStyle,
                example_responses: exampleResponses
            });

            onCharacterAdded();
            onClose();
            // Reset form
            setName('');
            setMovie('');
            setChatStyle('');
            setExampleResponses([]);
        } catch (error) {
            setError(error instanceof Error ? error.message : 'Failed to create character');
        } finally {
            setIsSubmitting(false);
        }
    };

    return (
        <AnimatePresence>
            {isOpen && (
                <div className="fixed inset-0 z-50 overflow-y-auto">
                    <div className="flex items-center justify-center min-h-screen px-4">
                        <motion.div
                            initial={{ opacity: 0 }}
                            animate={{ opacity: 1 }}
                            exit={{ opacity: 0 }}
                            className="fixed inset-0 bg-black/50"
                            onClick={onClose}
                        />
                        
                        <motion.div
                            initial={{ scale: 0.95, opacity: 0 }}
                            animate={{ scale: 1, opacity: 1 }}
                            exit={{ scale: 0.95, opacity: 0 }}
                            className="relative w-full max-w-lg bg-gray-800 rounded-xl shadow-lg p-6"
                        >
                            <button
                                onClick={onClose}
                                className="absolute right-4 top-4 text-gray-400 hover:text-white"
                            >
                                <XMarkIcon className="w-6 h-6" />
                            </button>

                            <h2 className="text-2xl font-bold text-white mb-6">Add New Character</h2>

                            <form onSubmit={handleSubmit} className="space-y-4">
                                <div>
                                    <label className="block text-sm font-medium text-gray-300 mb-1">
                                        Character Name
                                    </label>
                                    <input
                                        type="text"
                                        value={name}
                                        onChange={(e) => setName(e.target.value)}
                                        className="w-full p-2 bg-gray-700 rounded-lg text-white border border-gray-600 focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                                        required
                                    />
                                </div>

                                <div>
                                    <label className="block text-sm font-medium text-gray-300 mb-1">
                                        Movie/Show
                                    </label>
                                    <input
                                        type="text"
                                        value={movie}
                                        onChange={(e) => setMovie(e.target.value)}
                                        className="w-full p-2 bg-gray-700 rounded-lg text-white border border-gray-600 focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                                        required
                                    />
                                </div>

                                <div>
                                    <label className="block text-sm font-medium text-gray-300 mb-1">
                                        Chat Style
                                    </label>
                                    <input
                                        type="text"
                                        value={chatStyle}
                                        onChange={(e) => setChatStyle(e.target.value)}
                                        className="w-full p-2 bg-gray-700 rounded-lg text-white border border-gray-600 focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                                        placeholder="e.g., witty and sarcastic"
                                        required
                                    />
                                </div>

                                <div>
                                    <label className="block text-sm font-medium text-gray-300 mb-1">
                                        Example Responses
                                    </label>
                                    <div className="flex gap-2">
                                        <input
                                            type="text"
                                            value={exampleResponse}
                                            onChange={(e) => setExampleResponse(e.target.value)}
                                            className="flex-1 p-2 bg-gray-700 rounded-lg text-white border border-gray-600 focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                                            placeholder="Add an example response"
                                        />
                                        <button
                                            type="button"
                                            onClick={handleAddExample}
                                            className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700"
                                        >
                                            Add
                                        </button>
                                    </div>
                                    <div className="mt-2 space-y-2">
                                        {exampleResponses.map((response, index) => (
                                            <div
                                                key={index}
                                                className="flex items-center justify-between p-2 bg-gray-700 rounded-lg"
                                            >
                                                <span className="text-white text-sm">{response}</span>
                                                <button
                                                    type="button"
                                                    onClick={() => handleRemoveExample(index)}
                                                    className="text-red-400 hover:text-red-300"
                                                >
                                                    <XMarkIcon className="w-5 h-5" />
                                                </button>
                                            </div>
                                        ))}
                                    </div>
                                </div>

                                {error && (
                                    <div className="text-red-500 text-sm p-2 rounded-lg bg-red-500/10">
                                        {error}
                                    </div>
                                )}

                                <div className="flex justify-end gap-3 mt-6">
                                    <button
                                        type="button"
                                        onClick={onClose}
                                        className="px-4 py-2 text-gray-300 hover:text-white"
                                    >
                                        Cancel
                                    </button>
                                    <button
                                        type="submit"
                                        disabled={isSubmitting}
                                        className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 disabled:opacity-50 disabled:cursor-not-allowed"
                                    >
                                        {isSubmitting ? 'Creating...' : 'Create Character'}
                                    </button>
                                </div>
                            </form>
                        </motion.div>
                    </div>
                </div>
            )}
        </AnimatePresence>
    );
};

export default AddCharacterModal;
