import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';

interface AuthProps {
    onLoginSuccess: () => void;
}

const Auth: React.FC<AuthProps> = ({ onLoginSuccess }) => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [isLogin, setIsLogin] = useState(true);
    const [error, setError] = useState('');
    const [showSuccess, setShowSuccess] = useState(false);

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();
        setError('');
        setShowSuccess(false);

        try {
            const response = await fetch(`http://localhost:8000/auth/${isLogin ? 'login' : 'register'}`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ email, password }),
            });

            const data = await response.json();

            if (!response.ok) {
                throw new Error(data.detail || 'Authentication failed');
            }

            if (!isLogin) {
                // Show success message for registration
                setShowSuccess(true);
                // Clear form
                setEmail('');
                setPassword('');
                // Switch to login form after 2 seconds
                setTimeout(() => {
                    setShowSuccess(false);
                    setIsLogin(true);
                }, 2000);
                return;
            }

            if (data.access_token) {
                localStorage.setItem('token', data.access_token);
                onLoginSuccess();
            }
        } catch (error) {
            setError(error instanceof Error ? error.message : 'Authentication failed');
        }
    };

    return (
        <div 
            className="min-h-screen flex items-center justify-center p-4 relative bg-gradient-to-br from-gray-900 via-indigo-900 to-gray-900"
        >
            {/* Success Message Overlay */}
            <AnimatePresence>
                {showSuccess && (
                    <motion.div
                        initial={{ opacity: 0, scale: 0.5 }}
                        animate={{ opacity: 1, scale: 1 }}
                        exit={{ opacity: 0, scale: 0.5 }}
                        className="fixed inset-0 flex items-center justify-center z-50 bg-black/50 backdrop-blur-sm"
                    >
                        <motion.div
                            initial={{ y: 20 }}
                            animate={{ y: 0 }}
                            className="bg-gradient-to-br from-green-500 to-emerald-600 p-6 rounded-2xl shadow-xl text-white text-center"
                        >
                            <motion.div
                                animate={{ 
                                    rotate: [0, 10, -10, 10, 0],
                                    scale: [1, 1.2, 1] 
                                }}
                                transition={{ duration: 0.5 }}
                                className="text-4xl mb-2"
                            >
                                ðŸŽ‰
                            </motion.div>
                            <h3 className="text-xl font-bold mb-1">Account Created Successfully!</h3>
                            <p className="text-sm opacity-90">Redirecting to login...</p>
                        </motion.div>
                    </motion.div>
                )}
            </AnimatePresence>

            {/* Glass card effect */}
            <motion.div
                initial={{ scale: 0.95, opacity: 0 }}
                animate={{ scale: 1, opacity: 1 }}
                className="w-full max-w-md p-8 rounded-2xl bg-white/10 backdrop-blur-xl shadow-2xl border border-white/20"
            >
                <motion.div
                    initial={{ y: -20 }}
                    animate={{ y: 0 }}
                    className="text-center mb-8"
                >
                    <h2 className="text-3xl font-bold text-white mb-2">
                        {isLogin ? 'Welcome Back' : 'Create Account'}
                    </h2>
                    <p className="text-gray-300 text-sm">
                        {isLogin 
                            ? 'Sign in to continue chatting with your favorite characters' 
                            : 'Join us and start chatting with amazing characters'}
                    </p>
                </motion.div>

                <form onSubmit={handleSubmit} className="space-y-6">
                    <div>
                        <label htmlFor="email" className="block text-sm font-medium text-gray-300 mb-2">
                            Email Address
                        </label>
                        <motion.input
                            whileFocus={{ scale: 1.01 }}
                            id="email"
                            type="email"
                            value={email}
                            onChange={(e) => setEmail(e.target.value)}
                            className="w-full px-4 py-3 rounded-lg bg-gray-800/50 text-white placeholder-gray-400 border border-gray-700 focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:border-transparent transition-all duration-200"
                            placeholder="you@example.com"
                            required
                        />
                    </div>

                    <div>
                        <label htmlFor="password" className="block text-sm font-medium text-gray-300 mb-2">
                            Password
                        </label>
                        <motion.input
                            whileFocus={{ scale: 1.01 }}
                            id="password"
                            type="password"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            className="w-full px-4 py-3 rounded-lg bg-gray-800/50 text-white placeholder-gray-400 border border-gray-700 focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:border-transparent transition-all duration-200"
                            placeholder="Enter your password"
                            required
                        />
                    </div>

                    {error && (
                        <motion.div
                            initial={{ opacity: 0 }}
                            animate={{ opacity: 1 }}
                            className="p-4 rounded-lg bg-red-500/10 border border-red-500/20"
                        >
                            <p className="text-red-500 text-sm text-center">
                                {error}
                            </p>
                        </motion.div>
                    )}

                    <motion.button
                        whileHover={{ scale: 1.01 }}
                        whileTap={{ scale: 0.99 }}
                        type="submit"
                        className="w-full px-4 py-3 text-white text-sm font-medium bg-gradient-to-r from-indigo-600 to-indigo-700 rounded-lg hover:from-indigo-500 hover:to-indigo-600 focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:ring-offset-2 focus:ring-offset-gray-800 shadow-lg shadow-indigo-500/20 transition-all duration-200"
                    >
                        {isLogin ? 'Sign In' : 'Create Account'}
                    </motion.button>

                    <div className="text-center mt-6">
                        <button
                            type="button"
                            onClick={() => {
                                setIsLogin(!isLogin);
                                setError('');
                            }}
                            className="text-sm text-gray-400 hover:text-white transition-colors duration-200"
                        >
                            {isLogin 
                                ? "Don't have an account? Create one" 
                                : 'Already have an account? Sign in'}
                        </button>
                    </div>
                </form>
            </motion.div>
        </div>
    );
};

export default Auth;
