import React from 'react';
import { motion } from 'framer-motion';

interface VoiceIndicatorProps {
    isActive: boolean;
    type: 'listening' | 'speaking';
}

const VoiceIndicator: React.FC<VoiceIndicatorProps> = ({ isActive, type }) => {
    return (
        <div className="flex items-center space-x-2">
            {isActive && (
                <div className="flex space-x-1">
                    {[...Array(3)].map((_, i) => (
                        <motion.div
                            key={i}
                            className={`w-1 h-4 rounded-full ${
                                type === 'listening' ? 'bg-blue-500' : 'bg-purple-500'
                            }`}
                            animate={{
                                scaleY: [1, 1.5, 1],
                                opacity: [0.5, 1, 0.5]
                            }}
                            transition={{
                                duration: 1,
                                repeat: Infinity,
                                delay: i * 0.2
                            }}
                        />
                    ))}
                </div>
            )}
            <span className={`text-sm ${type === 'listening' ? 'text-blue-500' : 'text-purple-500'}`}>
                {isActive && (type === 'listening' ? 'Listening...' : 'Speaking...')}
            </span>
        </div>
    );
};

export default VoiceIndicator;
