import React, { useState, useEffect, useRef, useCallback } from 'react';
import { motion, AnimatePresence, LazyMotion, domAnimation } from 'framer-motion';
import { 
    PaperAirplaneIcon, 
    ChevronDownIcon,
    Bars3Icon,
    UserCircleIcon,
    ArrowLeftOnRectangleIcon,
    Cog6ToothIcon
} from '@heroicons/react/24/solid';
import MessageBubble from './MessageBubble';
import TypingIndicator from './TypingIndicator';
import { containerVariants, dropdownVariants } from '../styles/variants';
import { fetchCharacters, streamMessage, StreamResponse } from '../services/api';
import { CharacterSelect } from './CharacterSelect';

// Interfaces remain the same
interface Character {
    id: number;
    name: string;
    movie: string;
    chat_style?: string;
}

interface Message {
    text: string;
    isUser: boolean;
    id: string;
    isStreaming?: boolean;
    character?: Character;
    timestamp?: Date;
}

interface ChatHistory {
    id: string;
    title: string;
    lastMessage: string;
    timestamp: Date;
}

const Chat: React.FC = () => {
    const [messages, setMessages] = useState<Message[]>([]);
    const [input, setInput] = useState<string>('');
    const [characters, setCharacters] = useState<Character[]>([]);
    const [selectedCharacter, setSelectedCharacter] = useState<Character | null>(null);
    const [isDropdownOpen, setIsDropdownOpen] = useState(false);
    const [isLoading, setIsLoading] = useState(false);
    const [error, setError] = useState<string | null>(null);
    const [isSidebarOpen, setIsSidebarOpen] = useState(true);
    const [isUserMenuOpen, setIsUserMenuOpen] = useState(false);
    const [chatHistory, setChatHistory] = useState<ChatHistory[]>([]);
    const [selectedChat, setSelectedChat] = useState<string | null>(null);

    const messagesEndRef = useRef<HTMLDivElement>(null);
    const dropdownRef = useRef<HTMLDivElement>(null);
    const userMenuRef = useRef<HTMLDivElement>(null);

    // Original functionality remains the same
    const scrollToBottom = useCallback(() => {
        messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
    }, []);

    // Load characters and chat history
    useEffect(() => {
        loadCharacters();
        loadChatHistory();

        const handleClickOutside = (event: MouseEvent) => {
            if (dropdownRef.current && !dropdownRef.current.contains(event.target as Node)) {
                setIsDropdownOpen(false);
            }
            if (userMenuRef.current && !userMenuRef.current.contains(event.target as Node)) {
                setIsUserMenuOpen(false);
            }
        };

        document.addEventListener('mousedown', handleClickOutside);
        return () => document.removeEventListener('mousedown', handleClickOutside);
    }, []);

    useEffect(() => {
        scrollToBottom();
    }, [messages, isLoading, scrollToBottom]);

    // Existing functions remain the same
    const loadCharacters = async () => {
        try {
            const data = await fetchCharacters();
            setCharacters(data);
        } catch (error) {
            setError('Failed to load characters');
            console.error('Error fetching characters:', error);
        }
    };

    // Mock function to load chat history - replace with actual API call
    const loadChatHistory = () => {
        const mockHistory: ChatHistory[] = [
            {
                id: '1',
                title: 'Previous Chat 1',
                lastMessage: 'Last message from chat 1',
                timestamp: new Date()
            },
            {
                id: '2',
                title: 'Previous Chat 2',
                lastMessage: 'Last message from chat 2',
                timestamp: new Date(Date.now() - 86400000)
            }
        ];
        setChatHistory(mockHistory);
    };

    // Handle user menu actions
    const handleLogout = () => {
        // Add logout logic here
        console.log('Logging out...');
    };

    const handleSettings = () => {
        // Add settings logic here
        console.log('Opening settings...');
    };

    // Existing message handling logic remains the same
    const handleSendMessage = async (e: React.FormEvent) => {
        e.preventDefault();
        if (input.trim() && !isLoading) {
            const userMessageId = Date.now().toString();
            const userMessage = { 
                text: input, 
                isUser: true, 
                id: userMessageId,
                character: selectedCharacter || undefined,
                timestamp: new Date()
            };
            const botMessageId = (Date.now() + 1).toString();

            setMessages(prev => [...prev, userMessage, { 
                text: '', 
                isUser: false, 
                id: botMessageId,
                isStreaming: true,
                character: selectedCharacter || undefined,
                timestamp: new Date()
            }]);
            setInput('');
            setIsLoading(true);
            setError(null);

            try {
                streamMessage(
                    {
                        message: input,
                        character_id: selectedCharacter?.id,
                        user_id: 1
                    },
                    (data: StreamResponse) => {
                        if (data.error) {
                            setError(data.error);
                            setMessages(prev => prev.filter(msg => msg.id !== botMessageId));
                            setIsLoading(false);
                        } else {
                            setMessages(prev => prev.map(msg => 
                                msg.id === botMessageId 
                                    ? { 
                                        ...msg, 
                                        text: msg.text + (data.text || ''),
                                        isStreaming: !data.done 
                                    }
                                    : msg
                            ));

                            if (data.done) {
                                setIsLoading(false);
                            }
                            scrollToBottom();
                        }
                    }
                );
            } catch (error) {
                const errorMessage = error instanceof Error ? error.message : 'Failed to send message';
                setError(errorMessage);
                console.error('Error sending message:', error);
                setIsLoading(false);
            }
        }
    };

    const handleCharacterSelect = (character: Character) => {
        setSelectedCharacter(character);
        // Clear messages when switching characters
        setMessages([]);
    };

    if (!selectedCharacter) {
        return (
            <div className="h-screen flex flex-col">
                <h2 className="text-2xl font-bold text-center p-4">Choose your character</h2>
                <CharacterSelect onCharacterSelect={handleCharacterSelect} />
            </div>
        );
    }

    return (
        <LazyMotion features={domAnimation}>
            <div className="flex h-screen bg-gray-900">
                {/* Sidebar */}
                <motion.div 
                    initial={{ x: isSidebarOpen ? 0 : -320 }}
                    animate={{ x: isSidebarOpen ? 0 : -320 }}
                    transition={{ type: "spring", stiffness: 300, damping: 30 }}
                    className="w-80 bg-gray-800 border-r border-gray-700 flex flex-col"
                >
                    <div className="p-4 border-b border-gray-700">
                        <h2 className="text-xl font-semibold text-white">Chat History</h2>
                    </div>
                    <div className="flex-1 overflow-y-auto p-4">
                        {chatHistory.map((chat) => (
                            <motion.button
                                key={chat.id}
                                whileHover={{ backgroundColor: 'rgba(75, 85, 99, 0.4)' }}
                                onClick={() => setSelectedChat(chat.id)}
                                className={`w-full p-3 rounded-lg mb-2 text-left ${
                                    selectedChat === chat.id ? 'bg-gray-700' : 'hover:bg-gray-700'
                                } transition-colors duration-200`}
                            >
                                <h3 className="text-white font-medium">{chat.title}</h3>
                                <p className="text-gray-400 text-sm truncate">{chat.lastMessage}</p>
                                <span className="text-gray-500 text-xs">
                                    {chat.timestamp.toLocaleDateString()}
                                </span>
                            </motion.button>
                        ))}
                    </div>
                </motion.div>

                {/* Main Chat Area */}
                <div className="flex-1 flex flex-col">
                    {/* Top Bar */}
                    <div className="h-16 border-b border-gray-700 flex items-center justify-between px-4">
                        <button
                            onClick={() => setIsSidebarOpen(!isSidebarOpen)}
                            className="p-2 hover:bg-gray-700 rounded-lg transition-colors duration-200"
                        >
                            <Bars3Icon className="w-6 h-6 text-white" />
                        </button>

                        {/* Character Selection */}
                        <div className="flex-1 max-w-md mx-4" ref={dropdownRef}>
                            <button
                                onClick={() => setIsDropdownOpen(!isDropdownOpen)}
                                className="w-full p-2 text-left bg-gray-700 hover:bg-gray-600 rounded-lg flex items-center justify-between text-white transition-colors duration-200"
                            >
                                <span>{selectedCharacter ? `${selectedCharacter.name} from ${selectedCharacter.movie}` : 'Select a character'}</span>
                                <ChevronDownIcon className={`w-5 h-5 transition-transform duration-200 ${isDropdownOpen ? 'transform rotate-180' : ''}`} />
                            </button>

                            <AnimatePresence>
                                {isDropdownOpen && (
                                    <motion.div
                                        variants={dropdownVariants}
                                        initial="hidden"
                                        animate="visible"
                                        exit="hidden"
                                        className="absolute mt-2 w-full max-w-md bg-gray-700 rounded-lg shadow-xl z-10"
                                    >
                                        <div className="py-2">
                                            <button
                                                onClick={() => {
                                                    setSelectedCharacter(null);
                                                    setIsDropdownOpen(false);
                                                }}
                                                className="w-full px-4 py-2 text-left text-white hover:bg-gray-600 transition-colors duration-200"
                                            >
                                                Default Assistant
                                            </button>
                                            {characters.map((character) => (
                                                <button
                                                    key={character.id}
                                                    onClick={() => {
                                                        setSelectedCharacter(character);
                                                        setIsDropdownOpen(false);
                                                    }}
                                                    className="w-full px-4 py-2 text-left text-white hover:bg-gray-600 transition-colors duration-200"
                                                >
                                                    {character.name} from {character.movie}
                                                </button>
                                            ))}
                                        </div>
                                    </motion.div>
                                )}
                            </AnimatePresence>
                        </div>

                        {/* User Menu */}
                        <div className="relative" ref={userMenuRef}>
                            <button
                                onClick={() => setIsUserMenuOpen(!isUserMenuOpen)}
                                className="p-2 hover:bg-gray-700 rounded-full transition-colors duration-200"
                            >
                                <UserCircleIcon className="w-8 h-8 text-white" />
                            </button>

                            <AnimatePresence>
                                {isUserMenuOpen && (
                                    <motion.div
                                        variants={dropdownVariants}
                                        initial="hidden"
                                        animate="visible"
                                        exit="hidden"
                                        className="absolute right-0 mt-2 w-48 bg-gray-700 rounded-lg shadow-xl z-10"
                                    >
                                        <div className="py-2">
                                            <button
                                                onClick={handleSettings}
                                                className="w-full px-4 py-2 text-left text-white hover:bg-gray-600 transition-colors duration-200 flex items-center"
                                            >
                                                <Cog6ToothIcon className="w-5 h-5 mr-2" />
                                                Settings
                                            </button>
                                            <button
                                                onClick={handleLogout}
                                                className="w-full px-4 py-2 text-left text-white hover:bg-gray-600 transition-colors duration-200 flex items-center"
                                            >
                                                <ArrowLeftOnRectangleIcon className="w-5 h-5 mr-2" />
                                                Logout
                                            </button>
                                        </div>
                                    </motion.div>
                                )}
                            </AnimatePresence>
                        </div>
                    </div>

                    {/* Messages Area */}
                    <div className="flex-1 overflow-y-auto p-4">
                        {messages.length === 0 && (
                            <div className="flex flex-col items-center justify-center h-full text-gray-400">
                                <p className="text-xl mb-4">Welcome to the AI Chatbot!</p>
                                <p className="text-sm">Start a conversation with {selectedCharacter ? selectedCharacter.name : 'the AI Assistant'}</p>
                            </div>
                        )}
                        <div className="space-y-4">
                            <AnimatePresence initial={false}>
                                {messages.map((msg) => (
                                    <MessageBubble
                                        key={msg.id}
                                        message={msg.text}
                                        isUser={msg.isUser}
                                        isTyping={msg.isStreaming}
                                        character={msg.character}
                                    />
                                ))}
                            </AnimatePresence>
                            {isLoading && !messages.some(msg => msg.isStreaming) && (
                                <motion.div
                                    initial={{ opacity: 0 }}
                                    animate={{ opacity: 1 }}
                                    exit={{ opacity: 0 }}
                                >
                                    <TypingIndicator />
                                </motion.div>
                            )}
                            {error && (
                                <motion.div
                                    initial={{ opacity: 0 }}
                                    animate={{ opacity: 1 }}
                                    className="text-red-500 text-sm p-2 rounded-lg bg-red-500/10"
                                >
                                    {error}
                                </motion.div>
                            )}
                            <div ref={messagesEndRef} />
                        </div>
                    </div>

                    {/* Input Area */}
                    <div className="p-4 border-t border-gray-700">
                        <form onSubmit={handleSendMessage} className="flex gap-2">
                            <input
                                type="text"
                                value={input}
                                onChange={(e) => setInput(e.target.value)}
                                placeholder={selectedCharacter 
                                    ? `Chat with ${selectedCharacter.name}...` 
                                    : "Chat with AI Assistant..."
                                }
                                disabled={isLoading}
                                className="flex-1 p-3 rounded-lg bg-gray-700 text-white placeholder-gray-400 border border-gray-600 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent disabled:opacity-50 disabled:cursor-not-allowed"
                            />
                            <motion.button
                                type="submit"
                                whileHover={{ scale: 1.05 }}
                                whileTap={{ scale: 0.95 }}
                                disabled={isLoading || !input.trim()}
                                className="p-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2 focus:ring-offset-gray-800 transition-colors duration-200 disabled:opacity-50 disabled:cursor-not-allowed"
                            >
                                <PaperAirplaneIcon className="w-6 h-6" />
                            </motion.button>
                        </form>
                    </div>
                </div>
            </div>
        </LazyMotion>
    );
};

export default Chat;
