import React, { useEffect, useState } from 'react';
import { Character, getCharacters, selectCharacter } from '../services/api';

interface CharacterSelectProps {
    onCharacterSelect: (character: Character) => void;
}

export const CharacterSelect: React.FC<CharacterSelectProps> = ({ onCharacterSelect }) => {
    const [characters, setCharacters] = useState<Character[]>([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState<string | null>(null);

    useEffect(() => {
        const loadCharacters = async () => {
            try {
                const data = await getCharacters();
                setCharacters(data);
            } catch (err) {
                setError('Failed to load characters');
            } finally {
                setLoading(false);
            }
        };
        loadCharacters();
    }, []);

    const handleCharacterSelect = async (character: Character) => {
        try {
            await selectCharacter(character.id);
            onCharacterSelect(character);
        } catch (err) {
            setError('Failed to select character');
        }
    };

    if (loading) return <div className="text-gray-600">Loading characters...</div>;
    if (error) return <div className="text-red-500">{error}</div>;

    return (
        <div className="grid grid-cols-2 md:grid-cols-3 gap-4 p-4">
            {characters.map((character) => (
                <div
                    key={character.id}
                    className="p-4 border rounded-lg hover:bg-gray-50 cursor-pointer transition-colors"
                    onClick={() => handleCharacterSelect(character)}
                >
                    <h3 className="font-bold text-lg">{character.name}</h3>
                    <p className="text-gray-600">{character.movie}</p>
                    <p className="text-sm text-gray-500 mt-2">{character.chat_style}</p>
                </div>
            ))}
        </div>
    );
};
