import React, { useEffect, useState } from 'react';
import { motion } from 'framer-motion';
import { messageVariants } from '../styles/variants';
import TypingIndicator from './TypingIndicator';

interface Character {
    id: number;
    name: string;
    movie: string;
}

interface MessageBubbleProps {
    message: string;
    isUser: boolean;
    isTyping?: boolean;
    character?: Character;
}

const MessageBubble: React.FC<MessageBubbleProps> = ({ message, isUser, isTyping = false, character }) => {
    const [displayText, setDisplayText] = useState('');
    const [showTyping, setShowTyping] = useState(isTyping);

    useEffect(() => {
        if (isTyping) {
            setShowTyping(true);
        } else {
            setDisplayText(message);
            setShowTyping(false);
        }
    }, [message, isTyping]);

    return (
        <motion.div
            variants={messageVariants}
            initial="initial"
            animate="animate"
            exit="exit"
            className={`flex ${isUser ? 'justify-end' : 'justify-start'} mb-4`}
        >
            <div className={`flex flex-col ${isUser ? 'items-end' : 'items-start'}`}>
                {!isUser && character && (
                    <span className="text-sm text-gray-400 mb-1 ml-1">
                        {character.name}
                    </span>
                )}
                <div
                    className={`max-w-[70%] rounded-2xl px-6 py-3 ${
                        isUser
                            ? 'bg-blue-600 text-white'
                            : character 
                                ? 'bg-purple-700 text-white'
                                : 'bg-gray-700 text-gray-100'
                    } shadow-lg`}
                >
                    {showTyping && !message ? (
                        <TypingIndicator />
                    ) : (
                        <p className="text-sm md:text-base whitespace-pre-wrap break-words">
                            {displayText}
                        </p>
                    )}
                </div>
            </div>
        </motion.div>
    );
};

export default MessageBubble;
