import React, { useState, useEffect, useRef, useCallback } from 'react';
import { motion, AnimatePresence, LazyMotion, domAnimation } from 'framer-motion';
import { 
    PaperAirplaneIcon, 
    ChevronDownIcon,
    Bars3Icon,
    UserCircleIcon,
    ArrowLeftOnRectangleIcon,
    Cog6ToothIcon,
    UserGroupIcon,
    MicrophoneIcon,
    SpeakerWaveIcon,
    SpeakerXMarkIcon
} from '@heroicons/react/24/solid';
import MessageBubble from './MessageBubble';
import TypingIndicator from './TypingIndicator';
import VoiceIndicator from './VoiceIndicator';
import { CharacterSelect } from './CharacterSelect';
import { containerVariants, dropdownVariants } from '../styles/variants';
import { fetchCharacters, streamMessage, fetchChatMessages, fetchChatHistory, StreamResponse } from '../services/api';
import { Character } from '../services/api';
import { logout } from '../services/auth';
import { useSpeech } from '../hooks/useSpeech';

// Using Character interface imported from API service

interface Message {
    text: string;
    isUser: boolean;
    id: string;
    isStreaming?: boolean;
    character?: Character;
    timestamp?: Date;
}

interface ChatHistory {
    id: string;
    title: string;
    lastMessage: string;
    timestamp: Date;
}

const Chat: React.FC = () => {
    const [messages, setMessages] = useState<Message[]>([]);
    const [input, setInput] = useState<string>('');
    const [characters, setCharacters] = useState<Character[]>([]);
    const [selectedCharacter, setSelectedCharacter] = useState<Character | null>(null);
    const [isDropdownOpen, setIsDropdownOpen] = useState(false);
    const [isLoading, setIsLoading] = useState(false);
    const [error, setError] = useState<string | null>(null);
    const [isSidebarOpen, setIsSidebarOpen] = useState(true);
    const [isUserMenuOpen, setIsUserMenuOpen] = useState(false);
    const [chatHistory, setChatHistory] = useState<ChatHistory[]>([]);
    const [selectedChat, setSelectedChat] = useState<string | null>(null);
    const [isCharacterSelectOpen, setIsCharacterSelectOpen] = useState(false);
    const [isSpeechEnabled, setIsSpeechEnabled] = useState(true);

    const {
        startListening,
        stopListening,
        speak,
        isListening,
        isSpeaking,
        error: speechError
    } = useSpeech({
        onSpeechResult: (text) => {
            setInput(text);
            // Auto-send message when received from speech
            handleSendMessage(new Event('submit') as any);
        },
        onSpeechEnd: () => {
            // Handle any cleanup needed when speech recognition ends
        }
    });

    const messagesEndRef = useRef<HTMLDivElement>(null);
    const dropdownRef = useRef<HTMLDivElement>(null);
    const userMenuRef = useRef<HTMLDivElement>(null);

    const scrollToBottom = useCallback(() => {
        messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
    }, []);

    useEffect(() => {
        const defaultAssistant = {
            id: 0,
            name: 'AI Assistant',
            movie: 'Default',
            chat_style: 'helpful and friendly',
            example_responses: [],
            created_at: new Date().toISOString()
        };

        setSelectedCharacter(defaultAssistant);
        setMessages([{
            id: 'welcome',
            text: 'How can I help you today?',
            isUser: false,
            character: defaultAssistant,
            timestamp: new Date()
        }]);

        loadCharacters();
        loadAllChatHistory(); // Only load the history list, not the messages

        const handleClickOutside = (event: MouseEvent) => {
            if (dropdownRef.current && !dropdownRef.current.contains(event.target as Node)) {
                setIsDropdownOpen(false);
            }
            if (userMenuRef.current && !userMenuRef.current.contains(event.target as Node)) {
                setIsUserMenuOpen(false);
            }
        };

        document.addEventListener('mousedown', handleClickOutside);
        return () => document.removeEventListener('mousedown', handleClickOutside);
    }, []);

    useEffect(() => {
        scrollToBottom();
    }, [messages, isLoading, scrollToBottom]);

    const loadCharacters = async () => {
        try {
            const data = await fetchCharacters();
            setCharacters(data);
        } catch (error) {
            setError('Failed to load characters');
            console.error('Error fetching characters:', error);
        }
    };

    // New function to load only chat history list
    const loadAllChatHistory = async () => {
        try {
            const history = await fetchChatHistory();
            setChatHistory(history.map(chat => ({
                id: chat.id.toString(),
                title: `Chat ${chat.id}`,
                lastMessage: chat.last_message || 'No messages',
                timestamp: new Date(chat.timestamp)
            })));
        } catch (error) {
            console.error('Error loading chat history:', error);
            setError('Failed to load chat history list');
        }
    };

    // Modified to load specific chat history
    const loadChatMessages = async (chatId: string) => {
        try {
            setIsLoading(true);
            setError(null);

            const chatMessages = await fetchChatMessages(chatId);

            if (!chatMessages || !Array.isArray(chatMessages)) {
                throw new Error('Invalid chat history format');
            }

            const formattedMessages: Message[] = chatMessages.map((msg) => ({
                id: msg.id.toString(),
                text: msg.message,
                isUser: !msg.is_bot,
                character: characters.find(c => c.id === msg.character_id),
                timestamp: new Date(msg.timestamp)
            }));

            setMessages(formattedMessages);
            setSelectedChat(chatId);
        } catch (error) {
            console.error('Error loading chat messages:', error);
            setError('Failed to load chat messages. Please try again.');
        } finally {
            setIsLoading(false);
        }
    };

    const handleLogout = () => {
        logout();
    };

    const handleSettings = () => {
        console.log('Opening settings...');
    };

    const handleSendMessage = async (e: React.FormEvent) => {
        e.preventDefault();
        if (input.trim() && !isLoading) {
            const userMessageId = Date.now().toString();
            const userMessage = { 
                text: input, 
                isUser: true, 
                id: userMessageId,
                character: selectedCharacter || undefined,
                timestamp: new Date()
            };
            const botMessageId = (Date.now() + 1).toString();

            setMessages(prev => [...prev, userMessage, { 
                text: '', 
                isUser: false, 
                id: botMessageId,
                isStreaming: true,
                character: selectedCharacter || undefined,
                timestamp: new Date()
            }]);
            setInput('');
            setIsLoading(true);
            setError(null);

            try {
                let fullResponse = '';

                await streamMessage(
                    {
                        message: input,
                        character_id: selectedCharacter?.id,
                        user_id: 1
                    },
                    (data: StreamResponse) => {
                        if (data.error) {
                            setError(data.error);
                            setMessages(prev => prev.filter(msg => msg.id !== botMessageId));
                            setIsLoading(false);
                        } else {
                            fullResponse += data.text || '';
                            setMessages(prev => prev.map(msg => 
                                msg.id === botMessageId 
                                    ? { 
                                        ...msg, 
                                        text: fullResponse,
                                        isStreaming: !data.done 
                                    }
                                    : msg
                            ));

                            if (data.done) {
                                setIsLoading(false);
                                if (isSpeechEnabled && fullResponse) {
                                    speak(fullResponse);
                                }
                            }
                            scrollToBottom();
                        }
                    }
                );
            } catch (error) {
                const errorMessage = error instanceof Error ? error.message : 'Failed to send message';
                setError(errorMessage);
                console.error('Error sending message:', error);
                setIsLoading(false);
            }
        }
    };

    const handleCharacterSelect = async (character: Character) => {
        setSelectedCharacter(character);
        setIsCharacterSelectOpen(false);

        // Clear current messages and show welcome message
        setMessages([{
            id: 'welcome',
            text: character.id === 0 
                ? "Switched to AI Assistant. How can I help you?"
                : `Switched to ${character.name} from ${character.movie}. How can I assist you?`,
            isUser: false,
            character: character,
            timestamp: new Date()
        }]);

        // Clear selected chat since we're starting fresh
        setSelectedChat(null);
    };

    return (
        <LazyMotion features={domAnimation}>
            <div className="flex h-screen bg-gray-900">
                <motion.div 
                    initial={{ x: isSidebarOpen ? 0 : -320 }}
                    animate={{ x: isSidebarOpen ? 0 : -320 }}
                    transition={{ type: "spring", stiffness: 300, damping: 30 }}
                    className="w-80 bg-gray-800 border-r border-gray-700 flex flex-col"
                >
                    <div className="p-4 border-b border-gray-700">
                        <h2 className="text-xl font-semibold text-white">Chat History</h2>
                    </div>
                    <div className="flex-1 overflow-y-auto p-4">
                        {chatHistory.map((chat) => (
                            <motion.button
                                key={chat.id}
                                whileHover={{ backgroundColor: 'rgba(75, 85, 99, 0.4)' }}
                                onClick={() => loadChatMessages(chat.id)}
                                className={`w-full p-3 rounded-lg mb-2 text-left ${
                                    selectedChat === chat.id ? 'bg-gray-700' : 'hover:bg-gray-700'
                                } transition-colors duration-200`}
                            >
                                <h3 className="text-white font-medium">{chat.title}</h3>
                                <p className="text-gray-400 text-sm truncate">{chat.lastMessage}</p>
                                <span className="text-gray-500 text-xs">
                                    {chat.timestamp.toLocaleDateString()}
                                </span>
                            </motion.button>
                        ))}
                    </div>
                </motion.div>

                <div className="flex-1 flex flex-col">
                    <div className="h-16 border-b border-gray-700 flex items-center justify-between px-4">
                        <button
                            onClick={() => setIsSidebarOpen(!isSidebarOpen)}
                            className="p-2 hover:bg-gray-700 rounded-lg transition-colors duration-200"
                        >
                            <Bars3Icon className="w-6 h-6 text-white" />
                        </button>

                        <button
                            onClick={() => setIsCharacterSelectOpen(true)}
                            className="flex items-center space-x-2 p-2 bg-gray-700 hover:bg-gray-600 rounded-lg transition-colors duration-200 text-white"
                        >
                            <UserGroupIcon className="w-5 h-5" />
                            <span>{selectedCharacter ? selectedCharacter.name : 'Select Character'}</span>
                        </button>

                        <div className="relative" ref={userMenuRef}>
                            <button
                                onClick={() => setIsUserMenuOpen(!isUserMenuOpen)}
                                className="p-2 hover:bg-gray-700 rounded-full transition-colors duration-200"
                            >
                                <UserCircleIcon className="w-8 h-8 text-white" />
                            </button>

                            <AnimatePresence>
                                {isUserMenuOpen && (
                                    <motion.div
                                        variants={dropdownVariants}
                                        initial="hidden"
                                        animate="visible"
                                        exit="hidden"
                                        className="absolute right-0 mt-2 w-48 bg-gray-700 rounded-lg shadow-xl z-10"
                                    >
                                        <div className="py-2">
                                            <button
                                                onClick={handleSettings}
                                                className="w-full px-4 py-2 text-left text-white hover:bg-gray-600 transition-colors duration-200 flex items-center"
                                            >
                                                <Cog6ToothIcon className="w-5 h-5 mr-2" />
                                                Settings
                                            </button>
                                            <button
                                                onClick={handleLogout}
                                                className="w-full px-4 py-2 text-left text-white hover:bg-gray-600 transition-colors duration-200 flex items-center"
                                            >
                                                <ArrowLeftOnRectangleIcon className="w-5 h-5 mr-2" />
                                                Logout
                                            </button>
                                        </div>
                                    </motion.div>
                                )}
                            </AnimatePresence>
                        </div>
                    </div>

                    <div className="flex-1 overflow-y-auto p-4">
                        <div className="space-y-4">
                            <AnimatePresence initial={false}>
                                {messages.map((msg) => (
                                    <MessageBubble
                                        key={msg.id}
                                        message={msg.text}
                                        isUser={msg.isUser}
                                        isTyping={msg.isStreaming}
                                        character={msg.character}
                                    />
                                ))}
                            </AnimatePresence>
                            {(isListening || isSpeaking) && (
                                <div className="flex justify-center">
                                    <VoiceIndicator 
                                        isActive={isListening}
                                        type="listening"
                                    />
                                    <VoiceIndicator 
                                        isActive={isSpeaking}
                                        type="speaking"
                                    />
                                </div>
                            )}
                            {isLoading && !messages.some(msg => msg.isStreaming) && (
                                <motion.div
                                    initial={{ opacity: 0 }}
                                    animate={{ opacity: 1 }}
                                    exit={{ opacity: 0 }}
                                >
                                    <TypingIndicator />
                                </motion.div>
                            )}
                            {error && (
                                <motion.div
                                    initial={{ opacity: 0 }}
                                    animate={{ opacity: 1 }}
                                    className="text-red-500 text-sm p-2 rounded-lg bg-red-500/10"
                                >
                                    {error}
                                </motion.div>
                            )}
                            <div ref={messagesEndRef} />
                        </div>
                    </div>

                    <div className="p-4 border-t border-gray-700">
                        <form onSubmit={handleSendMessage} className="flex gap-2">
                            <div className="flex gap-2">
                                <motion.button
                                    type="button"
                                    whileHover={{ scale: 1.05 }}
                                    whileTap={{ scale: 0.95 }}
                                    onClick={() => {
                                        if (isListening) {
                                            stopListening();
                                        } else {
                                            startListening();
                                        }
                                    }}
                                    className={`p-3 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2 focus:ring-offset-gray-800 transition-colors duration-200 ${
                                        isListening 
                                            ? 'bg-blue-600 text-white' 
                                            : 'bg-gray-700 text-gray-300 hover:bg-gray-600'
                                    }`}
                                >
                                    <MicrophoneIcon className="w-6 h-6" />
                                </motion.button>
                                <motion.button
                                    type="button"
                                    whileHover={{ scale: 1.05 }}
                                    whileTap={{ scale: 0.95 }}
                                    onClick={() => setIsSpeechEnabled(!isSpeechEnabled)}
                                    className={`p-3 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2 focus:ring-offset-gray-800 transition-colors duration-200 ${
                                        isSpeechEnabled 
                                            ? 'bg-purple-600 text-white' 
                                            : 'bg-gray-700 text-gray-300 hover:bg-gray-600'
                                    }`}
                                >
                                    {isSpeechEnabled ? (
                                        <SpeakerWaveIcon className="w-6 h-6" />
                                    ) : (
                                        <SpeakerXMarkIcon className="w-6 h-6" />
                                    )}
                                </motion.button>
                            </div>
                            <input
                                type="text"
                                value={input}
                                onChange={(e) => setInput(e.target.value)}
                                placeholder={isListening ? 'Listening...' : 'Type your message...'}
                                className="flex-1 p-3 rounded-lg bg-gray-700 text-white placeholder-gray-400 border border-gray-600 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                            />
                            <motion.button
                                type="submit"
                                whileHover={{ scale: 1.05 }}
                                whileTap={{ scale: 0.95 }}
                                disabled={isLoading || !input.trim()}
                                className="p-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2 focus:ring-offset-gray-800 transition-colors duration-200 disabled:opacity-50 disabled:cursor-not-allowed"
                            >
                                <PaperAirplaneIcon className="w-6 h-6" />
                            </motion.button>
                        </form>
                    </div>
                </div>
            </div>

            <CharacterSelect
                isOpen={isCharacterSelectOpen}
                onCharacterSelect={handleCharacterSelect}
            />
        </LazyMotion>
    );
};

export default Chat;
