import React, { useEffect, useState } from 'react';
import { motion } from 'framer-motion';
import { Character, fetchCharacters } from '../services/api';
import {
    SparklesIcon,
    FilmIcon,
    BookOpenIcon,
    UserCircleIcon
} from '@heroicons/react/24/solid';

interface CharacterSelectProps {
    onCharacterSelect: (character: Character) => void;
    isOpen: boolean;
}

// Genre-based background gradients
const genreBackgrounds = {
    'action': 'from-red-500/20 to-orange-600/20',
    'drama': 'from-blue-500/20 to-purple-600/20',
    'comedy': 'from-yellow-500/20 to-green-600/20',
    'scifi': 'from-cyan-500/20 to-blue-600/20',
    'fantasy': 'from-purple-500/20 to-pink-600/20',
    'default': 'from-gray-600/20 to-gray-700/20'
};

// Function to determine genre based on movie/show name or chat style
const getGenre = (character: Character): keyof typeof genreBackgrounds => {
    const { movie, chat_style } = character;
    const text = (movie + ' ' + chat_style).toLowerCase();
    
    if (text.includes('action') || text.includes('adventure') || text.includes('hero')) return 'action';
    if (text.includes('drama') || text.includes('emotional')) return 'drama';
    if (text.includes('comedy') || text.includes('funny') || text.includes('humorous')) return 'comedy';
    if (text.includes('sci') || text.includes('future') || text.includes('space')) return 'scifi';
    if (text.includes('fantasy') || text.includes('magic') || text.includes('wizard')) return 'fantasy';
    return 'default';
};

// Function to generate avatar background based on name
const getAvatarColor = (name: string) => {
    const colors = [
        'bg-blue-500',
        'bg-purple-500',
        'bg-green-500',
        'bg-red-500',
        'bg-yellow-500',
        'bg-pink-500',
        'bg-indigo-500'
    ];
    const index = name.split('').reduce((acc, char) => acc + char.charCodeAt(0), 0) % colors.length;
    return colors[index];
};

export const CharacterSelect: React.FC<CharacterSelectProps> = ({ onCharacterSelect, isOpen }) => {
    const [characters, setCharacters] = useState<Character[]>([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState<string | null>(null);
    const [searchTerm, setSearchTerm] = useState('');

    useEffect(() => {
        const loadCharacters = async () => {
            try {
                setLoading(true);
                const data = await fetchCharacters();
                setCharacters(data);
            } catch (err) {
                setError('Failed to load characters');
            } finally {
                setLoading(false);
            }
        };

        if (isOpen) {
            loadCharacters();
        }
    }, [isOpen]);

    const filteredCharacters = characters.filter(char => 
        char.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
        char.movie.toLowerCase().includes(searchTerm.toLowerCase()) ||
        char.chat_style.toLowerCase().includes(searchTerm.toLowerCase())
    );

    if (!isOpen) return null;

    if (loading) {
        return (
            <div className="fixed inset-0 bg-gray-900 bg-opacity-75 flex items-center justify-center">
                <motion.div
                    initial={{ scale: 0.9, opacity: 0 }}
                    animate={{ scale: 1, opacity: 1 }}
                    className="bg-gray-800 p-6 rounded-lg shadow-xl"
                >
                    <div className="flex items-center space-x-4">
                        <SparklesIcon className="w-8 h-8 text-blue-500 animate-pulse" />
                        <p className="text-white text-lg">Loading characters...</p>
                    </div>
                </motion.div>
            </div>
        );
    }

    if (error) {
        return (
            <div className="fixed inset-0 bg-gray-900 bg-opacity-75 flex items-center justify-center">
                <div className="bg-gray-800 p-6 rounded-lg shadow-xl">
                    <p className="text-red-500">{error}</p>
                </div>
            </div>
        );
    }

    return (
        <div className="fixed inset-0 bg-gray-900 bg-opacity-75 flex items-center justify-center p-4">
            <motion.div
                initial={{ scale: 0.9, opacity: 0 }}
                animate={{ scale: 1, opacity: 1 }}
                className="bg-gray-800 rounded-lg p-6 w-full max-w-4xl max-h-[90vh] overflow-hidden flex flex-col"
            >
                <div className="mb-6">
                    <input
                        type="text"
                        placeholder="Search characters..."
                        value={searchTerm}
                        onChange={(e) => setSearchTerm(e.target.value)}
                        className="w-full p-3 rounded-lg bg-gray-700 text-white placeholder-gray-400 border border-gray-600 focus:outline-none focus:ring-2 focus:ring-blue-500"
                    />
                </div>
                
                <div className="overflow-y-auto grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4 pr-2">
                    {filteredCharacters.map((character) => {
                        const genre = getGenre(character);
                        const avatarColor = getAvatarColor(character.name);
                        
                        return (
                            <motion.button
                                key={character.id}
                                onClick={() => onCharacterSelect(character)}
                                className={`p-4 rounded-lg hover:bg-gray-600/50 transition-all text-left relative overflow-hidden bg-gradient-to-br ${genreBackgrounds[genre]} backdrop-blur-sm border border-gray-700/50`}
                                whileHover={{ 
                                    scale: 1.02,
                                    transition: { duration: 0.2 }
                                }}
                                whileTap={{ scale: 0.98 }}
                            >
                                <div className="flex items-start space-x-4">
                                    <div className={`w-12 h-12 rounded-full flex items-center justify-center ${avatarColor} shrink-0`}>
                                        <UserCircleIcon className="w-8 h-8 text-white" />
                                    </div>
                                    <div className="flex-1">
                                        <h3 className="text-lg font-semibold text-white">{character.name}</h3>
                                        <div className="flex items-center text-gray-300 text-sm mt-1">
                                            <FilmIcon className="w-4 h-4 mr-1" />
                                            {character.movie}
                                        </div>
                                        <p className="text-gray-400 text-sm mt-2">
                                            {character.chat_style}
                                        </p>
                                        {character.example_responses && character.example_responses.length > 0 && (
                                            <div className="mt-3 text-sm text-gray-400">
                                                <p className="italic">"{character.example_responses[0]}"</p>
                                            </div>
                                        )}
                                    </div>
                                </div>
                            </motion.button>
                        );
                    })}
                </div>
            </motion.div>
        </div>
    );
};

export default CharacterSelect;
