import React, { useState, useEffect, useRef, useCallback } from 'react';
import { motion, AnimatePresence, LazyMotion, domAnimation } from 'framer-motion';
import { PaperAirplaneIcon, ChevronDownIcon } from '@heroicons/react/24/solid';
import MessageBubble from './MessageBubble';
import TypingIndicator from './TypingIndicator';
import { containerVariants } from '../styles/variants';
import { fetchCharacters, streamMessage, StreamResponse } from '../services/api';

interface Character {
    id: number;
    name: string;
    movie: string;
    chat_style?: string;
}

interface Message {
    text: string;
    isUser: boolean;
    id: string;
    isStreaming?: boolean;
    character?: Character;
}

const Chat: React.FC = () => {
    const [messages, setMessages] = useState<Message[]>([]);
    const [input, setInput] = useState<string>('');
    const [characters, setCharacters] = useState<Character[]>([]);
    const [selectedCharacter, setSelectedCharacter] = useState<Character | null>(null);
    const [isDropdownOpen, setIsDropdownOpen] = useState(false);
    const [isLoading, setIsLoading] = useState(false);
    const [error, setError] = useState<string | null>(null);
    const messagesEndRef = useRef<HTMLDivElement>(null);
    const dropdownRef = useRef<HTMLDivElement>(null);

    const scrollToBottom = useCallback(() => {
        messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
    }, []);

    const loadCharacters = async () => {
        try {
            const data = await fetchCharacters();
            setCharacters(data);
            console.log('Characters loaded:', data);
        } catch (error) {
            setError('Failed to load characters');
            console.error('Error fetching characters:', error);
        }
    };

    const handleSendMessage = async (e: React.FormEvent) => {
        e.preventDefault();
        if (input.trim() && !isLoading) {
            const userMessageId = Date.now().toString();
            const userMessage = { 
                text: input, 
                isUser: true, 
                id: userMessageId,
                character: selectedCharacter || undefined
            };
            const botMessageId = (Date.now() + 1).toString();

            setMessages(prev => [...prev, userMessage, { 
                text: '', 
                isUser: false, 
                id: botMessageId,
                isStreaming: true,
                character: selectedCharacter || undefined
            }]);
            setInput('');
            setIsLoading(true);
            setError(null);

            try {
                streamMessage(
                    {
                        message: input,
                        character_id: selectedCharacter?.id,
                        user_id: 1 // Using default user_id for now
                    },
                    (data: StreamResponse) => {
                        if (data.error) {
                            setError(data.error);
                            setMessages(prev => prev.filter(msg => msg.id !== botMessageId));
                            setIsLoading(false);
                        } else {
                            setMessages(prev => prev.map(msg => 
                                msg.id === botMessageId 
                                    ? { 
                                        ...msg, 
                                        text: msg.text + (data.text || ''),
                                        isStreaming: !data.done 
                                    }
                                    : msg
                            ));

                            if (data.done) {
                                setIsLoading(false);
                            }
                            scrollToBottom();
                        }
                    }
                );
            } catch (error) {
                const errorMessage = error instanceof Error ? error.message : 'Failed to send message';
                setError(errorMessage);
                console.error('Error sending message:', error);
                setIsLoading(false);
            }
        }
    };

    useEffect(() => {
        loadCharacters();

        const handleClickOutside = (event: MouseEvent) => {
            if (dropdownRef.current && !dropdownRef.current.contains(event.target as Node)) {
                setIsDropdownOpen(false);
            }
        };

        document.addEventListener('mousedown', handleClickOutside);
        return () => document.removeEventListener('mousedown', handleClickOutside);
    }, []);

    useEffect(() => {
        scrollToBottom();
    }, [messages, isLoading, scrollToBottom]);

    return (
        <LazyMotion features={domAnimation}>
            <div className="flex items-center justify-center min-h-screen bg-gray-900 p-4">
                <motion.div 
                    variants={containerVariants}
                    initial="hidden"
                    animate="visible"
                    className="w-full max-w-4xl rounded-xl bg-gray-800 shadow-2xl overflow-hidden"
                >
                    <div className="p-4 border-b border-gray-700">
                        <div className="relative" ref={dropdownRef}>
                            <button
                                onClick={() => setIsDropdownOpen(!isDropdownOpen)}
                                className="w-full p-3 text-left bg-gray-700 hover:bg-gray-600 rounded-lg flex items-center justify-between text-white transition-colors duration-200"
                            >
                                <span>{selectedCharacter ? `${selectedCharacter.name} from ${selectedCharacter.movie}` : 'Select a character'}</span>
                                <ChevronDownIcon className={`w-5 h-5 transition-transform duration-200 ${isDropdownOpen ? 'transform rotate-180' : ''}`} />
                            </button>

                            <div>
                                {isDropdownOpen && (
                                    <AnimatePresence>
                                        <motion.div
                                            initial={{ opacity: 0, y: -10 }}
                                            animate={{ opacity: 1, y: 0 }}
                                            exit={{ opacity: 0, y: -10 }}
                                            className="absolute w-full mt-2 py-2 bg-gray-700 rounded-lg shadow-xl z-10"
                                        >
                                            <motion.button
                                                whileHover={{ backgroundColor: 'rgba(75, 85, 99, 1)' }}
                                                onClick={() => {
                                                    setSelectedCharacter(null);
                                                    setIsDropdownOpen(false);
                                                }}
                                                className="w-full px-4 py-2 text-left text-white hover:bg-gray-600 transition-colors duration-200"
                                            >
                                                Default Assistant
                                            </motion.button>
                                            {characters.map((character) => (
                                                <motion.button
                                                    key={character.id}
                                                    whileHover={{ backgroundColor: 'rgba(75, 85, 99, 1)' }}
                                                    onClick={() => {
                                                        setSelectedCharacter(character);
                                                        setIsDropdownOpen(false);
                                                    }}
                                                    className="w-full px-4 py-2 text-left text-white hover:bg-gray-600 transition-colors duration-200"
                                                >
                                                    {character.name} from {character.movie}
                                                </motion.button>
                                            ))}
                                        </motion.div>
                                    </AnimatePresence>
                                )}
                            </div>
                        </div>
                    </div>

                    <div className="h-[60vh] overflow-y-auto p-4 bg-gray-800">
                        <div className="space-y-4">
                            <div>
                                {messages.length > 0 && (
                                    <AnimatePresence initial={false}>
                                        {messages.map((msg) => (
                                            <MessageBubble
                                                key={msg.id}
                                                message={msg.text}
                                                isUser={msg.isUser}
                                                isTyping={msg.isStreaming}
                                                character={msg.character}
                                            />
                                        ))}
                                    </AnimatePresence>
                                )}
                                {isLoading && !messages.some(msg => msg.isStreaming) && (
                                    <motion.div
                                        initial={{ opacity: 0 }}
                                        animate={{ opacity: 1 }}
                                        exit={{ opacity: 0 }}
                                    >
                                        <TypingIndicator />
                                    </motion.div>
                                )}
                            </div>
                            {error && (
                                <motion.div
                                    initial={{ opacity: 0 }}
                                    animate={{ opacity: 1 }}
                                    className="text-red-500 text-sm p-2 rounded-lg bg-red-500/10"
                                >
                                    {error}
                                </motion.div>
                            )}
                            <div ref={messagesEndRef} />
                        </div>
                    </div>

                    <div className="p-4 border-t border-gray-700">
                        <form onSubmit={handleSendMessage} className="flex gap-2">
                            <input
                                type="text"
                                value={input}
                                onChange={(e) => setInput(e.target.value)}
                                placeholder={selectedCharacter 
                                    ? `Chat with ${selectedCharacter.name}...` 
                                    : "Chat with AI Assistant..."
                                }
                                disabled={isLoading}
                                className="flex-1 p-3 rounded-lg bg-gray-700 text-white placeholder-gray-400 border border-gray-600 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent disabled:opacity-50 disabled:cursor-not-allowed"
                            />
                            <motion.button
                                type="submit"
                                whileHover={{ scale: 1.05 }}
                                whileTap={{ scale: 0.95 }}
                                disabled={isLoading || !input.trim()}
                                className="p-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2 focus:ring-offset-gray-800 transition-colors duration-200 disabled:opacity-50 disabled:cursor-not-allowed"
                            >
                                <PaperAirplaneIcon className="w-6 h-6" />
                            </motion.button>
                        </form>
                    </div>
                </motion.div>
            </div>
        </LazyMotion>
    );
};

export default Chat;
