import React, { useEffect, useState } from 'react';
import { motion } from 'framer-motion';
import { Character, fetchCharacters } from '../services/api';

interface CharacterSelectProps {
    onCharacterSelect: (character: Character) => void;
    isOpen: boolean;
}

export const CharacterSelect: React.FC<CharacterSelectProps> = ({ onCharacterSelect, isOpen }) => {
    const [characters, setCharacters] = useState<Character[]>([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState<string | null>(null);

    useEffect(() => {
        const loadCharacters = async () => {
            try {
                setLoading(true);
                const data = await fetchCharacters();
                setCharacters(data);
            } catch (err) {
                setError('Failed to load characters');
            } finally {
                setLoading(false);
            }
        };

        if (isOpen) {
            loadCharacters();
        }
    }, [isOpen]);

    if (!isOpen) return null;

    if (loading) {
        return (
            <div className="fixed inset-0 bg-gray-900 bg-opacity-75 flex items-center justify-center">
                <motion.div
                    initial={{ scale: 0.9, opacity: 0 }}
                    animate={{ scale: 1, opacity: 1 }}
                    className="bg-gray-800 p-6 rounded-lg shadow-xl"
                >
                    <div className="flex items-center space-x-4">
                        <div className="w-8 h-8 border-4 border-blue-500 border-t-transparent rounded-full animate-spin" />
                        <p className="text-white text-lg">Loading characters...</p>
                    </div>
                </motion.div>
            </div>
        );
    }

    if (error) {
        return (
            <div className="fixed inset-0 bg-gray-900 bg-opacity-75 flex items-center justify-center">
                <div className="bg-gray-800 p-6 rounded-lg shadow-xl">
                    <p className="text-red-500">{error}</p>
                </div>
            </div>
        );
    }

    return (
        <div className="fixed inset-0 bg-gray-900 bg-opacity-75 flex items-center justify-center p-4">
            <motion.div
                initial={{ scale: 0.9, opacity: 0 }}
                animate={{ scale: 1, opacity: 1 }}
                className="bg-gray-800 rounded-lg p-6 w-full max-w-4xl max-h-[90vh] overflow-y-auto"
            >
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                    {characters.map((character) => (
                        <motion.button
                            key={character.id}
                            onClick={() => onCharacterSelect(character)}
                            className="p-4 bg-gray-700 rounded-lg hover:bg-gray-600 transition-colors text-left"
                            whileHover={{ scale: 1.02 }}
                            whileTap={{ scale: 0.98 }}
                        >
                            <h3 className="text-lg font-semibold text-white">{character.name}</h3>
                            <p className="text-gray-300">{character.movie}</p>
                            <p className="text-gray-400 text-sm mt-2">{character.chat_style}</p>
                        </motion.button>
                    ))}
                </div>
            </motion.div>
        </div>
    );
};
