import axios from 'axios';

export const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:8000';

export interface ChatMessage {
    message: string;
    character_id?: number;
    user_id: number;
    chat_id?: string;
    timestamp: string;
}

export interface ChatResponse {
    response: string;
}

export interface Character {
    id: number;
    name: string;
    movie: string;
    chat_style: string;
    example_responses: string[];
    created_at: string;
    updated_at?: string;
}

export interface StreamResponse {
    text?: string;
    done: boolean;
    error?: string;
}

export interface ChatHistoryMessage {
    id: number;
    message: string;
    user_id: number;
    character_id?: number;
    is_bot: boolean;
    timestamp: string;
    last_message?: string; // Added this field
    chat_id?: string;
}

export interface GroupedChatMessage {
    id: string;
    messages: ChatHistoryMessage[];
    timestamp: string;
    character?: Character;
}

export const fetchChatMessages = async (chatId: string): Promise<ChatHistoryMessage[]> => {
    try {
        const response = await axios.get(`${API_URL}/messages/chat/${chatId}`, {
            headers: getAuthHeaders()
        });
        return response.data;
    } catch (error) {
        console.error('Error fetching chat messages:', error);
        throw error;
    }
};

// Helper function to group messages by chat session
export const groupMessagesByChat = (messages: ChatHistoryMessage[]): GroupedChatMessage[] => {
    const groupedMessages = messages.reduce((acc, message) => {
        const key = message.chat_id || message.id.toString();
        if (!acc[key]) {
            acc[key] = {
                id: key,
                messages: [],
                timestamp: message.timestamp,
            };
        }
        acc[key].messages.push(message);
        return acc;
    }, {} as Record<string, GroupedChatMessage>);

    return Object.values(groupedMessages).sort(
        (a, b) => new Date(b.timestamp).getTime() - new Date(a.timestamp).getTime()
    );
};

export const fetchCharacters = async (): Promise<Character[]> => {
    try {
        const response = await fetch(`${API_URL}/characters`, {
            headers: getAuthHeaders()
        });
        if (!response.ok) {
            throw new Error(`HTTP error! status: ${response.status}`);
        }
        const data = await response.json();
        return data;
    } catch (error) {
        console.error('Error fetching characters:', error);
        throw new Error('Failed to fetch characters');
    }
};

export const sendMessage = async (message: ChatMessage): Promise<ChatResponse> => {
    try {
        const response = await axios.post(`${API_URL}/chat`, message, {
            headers: getAuthHeaders()
        });
        return response.data;
    } catch (error) {
        console.error('Error sending message:', error);
        throw error;
    }
};

export const streamMessage = async (
    message: ChatMessage,
    onMessage: (data: StreamResponse) => void
) => {
    let retryCount = 0;
    const MAX_RETRIES = 3;
    const RETRY_DELAY = 1000;
    let messageBuffer = '';
    let lastChunkTime = Date.now();
    const CHUNK_TIMEOUT = 10000;

    const connect = () => {
        const params = new URLSearchParams({
            message: message.message,
            ...(message.character_id && { character_id: message.character_id.toString() }),
            ...(message.chat_id && { chat_session: message.chat_id })
        });

        const token = localStorage.getItem('token');
        const eventSource = new EventSource(
            `${API_URL}/chat?${params}`,
            { withCredentials: true }
        );

        eventSource.onmessage = (event) => {
            try {
                const data = JSON.parse(event.data) as StreamResponse;
                lastChunkTime = Date.now();

                if (data.error) {
                    console.error('Stream error:', data.error);
                    eventSource.close();
                    onMessage({ text: '', done: true, error: data.error });
                    return;
                }

                if (data.text?.trim()) {
                    messageBuffer += data.text;
                    onMessage({ text: data.text, done: false });
                }

                if (data.done) {
                    eventSource.close();
                    if (messageBuffer.trim()) {
                        onMessage({ text: '', done: true });
                    }
                }
            } catch (error) {
                console.error('Error parsing message:', error);
                eventSource.close();
                onMessage({ text: '', done: true, error: 'Failed to parse response' });
            }
        };

        eventSource.onerror = async (error) => {
            const currentTime = Date.now();
            console.error('EventSource error:', error);
            eventSource.close();

            if (currentTime - lastChunkTime > CHUNK_TIMEOUT) {
                onMessage({ text: '', done: true, error: 'Response timeout. Please try again.' });
                return;
            }

            if (retryCount < MAX_RETRIES) {
                retryCount++;
                console.log(`Retrying connection (${retryCount}/${MAX_RETRIES})...`);
                await new Promise(resolve => setTimeout(resolve, RETRY_DELAY));
                connect();
            } else {
                onMessage({ text: '', done: true, error: 'Connection failed after multiple attempts.' });
            }
        };

        return () => {
            eventSource.close();
        };
    };

    return connect();
};

export const createCharacter = async (character: Omit<Character, 'id' | 'created_at' | 'updated_at'>): Promise<Character> => {
    try {
        const response = await fetch(`${API_URL}/characters`, {
            method: 'POST',
            headers: getAuthHeaders(),
            body: JSON.stringify(character),
        });

        if (!response.ok) {
            const errorData = await response.json();
            throw new Error(errorData.detail || `HTTP error! status: ${response.status}`);
        }

        return await response.json();
    } catch (error) {
        console.error('Error creating character:', error);
        throw error;
    }
};

export const updateCharacter = async (id: number, character: Partial<Character>): Promise<Character> => {
    try {
        const response = await fetch(`${API_URL}/characters/${id}`, {
            method: 'PUT',
            headers: getAuthHeaders(),
            body: JSON.stringify(character),
        });

        if (!response.ok) {
            const errorData = await response.json();
            throw new Error(errorData.detail || `HTTP error! status: ${response.status}`);
        }

        return await response.json();
    } catch (error) {
        console.error('Error updating character:', error);
        throw error;
    }
};

export const deleteCharacter = async (id: number): Promise<void> => {
    try {
        const response = await fetch(`${API_URL}/characters/${id}`, {
            method: 'DELETE',
            headers: getAuthHeaders(),
        });

        if (!response.ok) {
            const errorData = await response.json();
            throw new Error(errorData.detail || `HTTP error! status: ${response.status}`);
        }
    } catch (error) {
        console.error('Error deleting character:', error);
        throw error;
    }
};

export const getCharacters = async (): Promise<Character[]> => {
    const response = await fetch(`${API_URL}/characters`, {
        headers: getAuthHeaders(),
    });
    if (!response.ok) throw new Error('Failed to fetch characters');
    return response.json();
};

export const selectCharacter = async (characterId: number): Promise<void> => {
    const response = await fetch(`${API_URL}/characters/${characterId}/select`, {
        method: 'POST',
        headers: getAuthHeaders(),
    });
    if (!response.ok) throw new Error('Failed to select character');
};

export const fetchChatHistory = async (
    userId: number = 1,
    characterId?: number,
    limit: number = 50,
    offset: number = 0
): Promise<ChatHistoryMessage[]> => {
    try {
        let url = `${API_URL}/messages/${userId}?limit=${limit}&offset=${offset}`;
        if (characterId) {
            url += `&character_id=${characterId}`;
        }
        const response = await axios.get(url, {
            headers: getAuthHeaders()
        });
        return response.data;
    } catch (error) {
        console.error('Error fetching chat history:', error);
        throw error;
    }
};

function getAuthHeaders() {
    const token = localStorage.getItem('token');
    return {
        'Content-Type': 'application/json',
        ...(token && { 'Authorization': `Bearer ${token}` })
    };
}
