import React, { useState, useEffect, useRef } from 'react';
import { motion, AnimatePresence, LayoutGroup } from 'framer-motion';
import { PaperAirplaneIcon, ChevronDownIcon } from '@heroicons/react/24/solid';
import { sendMessage, fetchCharacters } from '../services/api';
import { messageVariants, dropdownVariants, containerVariants } from '../styles/variants';

interface Character {
    id: number;
    name: string;
    movie: string;
}

interface Message {
    text: string;
    isUser: boolean;
    id: string;
    isLoading?: boolean;
}

const TypingAnimation: React.FC = () => (
    <div className="flex space-x-2 p-3">
        <div className="w-2 h-2 bg-gray-400 rounded-full typing-dot" />
        <div className="w-2 h-2 bg-gray-400 rounded-full typing-dot" />
        <div className="w-2 h-2 bg-gray-400 rounded-full typing-dot" />
    </div>
);

const Chat: React.FC = () => {
    const [messages, setMessages] = useState<Message[]>([]);
    const [input, setInput] = useState<string>('');
    const [characters, setCharacters] = useState<Character[]>([]);
    const [selectedCharacter, setSelectedCharacter] = useState<Character | null>(null);
    const [isDropdownOpen, setIsDropdownOpen] = useState(false);
    const [isLoading, setIsLoading] = useState(false);
    const [error, setError] = useState<string | null>(null);
    const messagesEndRef = useRef<HTMLDivElement>(null);
    const dropdownRef = useRef<HTMLDivElement>(null);

    const scrollToBottom = () => {
        messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
    };

    const loadCharacters = async () => {
        try {
            const data = await fetchCharacters();
            setCharacters(data);
        } catch (error) {
            setError('Failed to load characters. Please try again later.');
            console.error('Error fetching characters:', error);
        }
    };

    const handleSendMessage = async (e: React.FormEvent) => {
        e.preventDefault();
        if (input.trim() && !isLoading) {
            const userMessageId = Date.now().toString();
            const userMessage = { text: input, isUser: true, id: userMessageId };

            setMessages(prev => [...prev, userMessage]);
            setInput('');
            setIsLoading(true);
            setError(null);

            try {
                const response = await sendMessage({
                    message: input,
                    character_id: selectedCharacter?.id
                });

                // Add loading message while waiting for response
                const loadingMessage: Message = {
                    id: 'loading',
                    text: '',
                    isUser: false,
                    isLoading: true
                };

                setMessages(prev => [...prev, loadingMessage]);
                scrollToBottom();

                // Simulate typing effect
                await new Promise(resolve => setTimeout(resolve, 500));

                // Replace loading message with actual response
                setMessages(prev => 
                    prev.filter(msg => msg.id !== 'loading').concat({
                        text: response.response,
                        isUser: false,
                        id: (Date.now() + 1).toString()
                    })
                );
            } catch (error) {
                setError('Failed to send message. Please try again.');
                console.error('Error sending message:', error);
            } finally {
                setIsLoading(false);
                scrollToBottom();
            }
        }
    };

    useEffect(() => {
        loadCharacters();

        const handleClickOutside = (event: MouseEvent) => {
            if (dropdownRef.current && !dropdownRef.current.contains(event.target as Node)) {
                setIsDropdownOpen(false);
            }
        };

        document.addEventListener('mousedown', handleClickOutside);
        return () => document.removeEventListener('mousedown', handleClickOutside);
    }, []);

    useEffect(() => {
        scrollToBottom();
    }, [messages]);

    return (
        <LayoutGroup>
            <div className="flex items-center justify-center min-h-screen bg-gray-900 p-4">
                <motion.div 
                    variants={containerVariants}
                    initial="hidden"
                    animate="visible"
                    className="w-full max-w-4xl rounded-xl bg-gray-800 shadow-2xl overflow-hidden"
                >
                    <div className="p-4 border-b border-gray-700">
                        <div className="relative" ref={dropdownRef}>
                            <button
                                onClick={() => setIsDropdownOpen(!isDropdownOpen)}
                                className="w-full p-3 text-left bg-gray-700 hover:bg-gray-600 rounded-lg flex items-center justify-between text-white transition-colors duration-200"
                            >
                                <span>{selectedCharacter ? `${selectedCharacter.name} from ${selectedCharacter.movie}` : 'Select a character'}</span>
                                <ChevronDownIcon className={`w-5 h-5 transition-transform duration-200 ${isDropdownOpen ? 'transform rotate-180' : ''}`} />
                            </button>

                            {isDropdownOpen && (
                                <motion.div
                                    variants={dropdownVariants}
                                    initial="hidden"
                                    animate="visible"
                                    exit="hidden"
                                    className="absolute w-full mt-2 py-2 bg-gray-700 rounded-lg shadow-xl z-10"
                                >
                                    {characters.map((character) => (
                                        <motion.button
                                            key={character.id}
                                            whileHover={{ backgroundColor: 'rgba(75, 85, 99, 1)' }}
                                            onClick={() => {
                                                setSelectedCharacter(character);
                                                setIsDropdownOpen(false);
                                            }}
                                            className="w-full px-4 py-2 text-left text-white hover:bg-gray-600 transition-colors duration-200"
                                        >
                                            {character.name} from {character.movie}
                                        </motion.button>
                                    ))}
                                </motion.div>
                            )}
                        </div>
                    </div>

                    <div className="h-[60vh] overflow-y-auto p-4 bg-gray-800">
                        <div className="space-y-4">
                            {messages.map((msg) => (
                                <motion.div
                                    key={msg.id}
                                    variants={messageVariants}
                                    initial="initial"
                                    animate="animate"
                                    exit="exit"
                                    layout
                                    className={`flex ${msg.isUser ? 'justify-end' : 'justify-start'} message-fade-in`}
                                >
                                    {msg.isLoading ? (
                                        <div className="bg-gray-700 rounded-2xl">
                                            <TypingAnimation />
                                        </div>
                                    ) : (
                                        <div
                                            className={`max-w-[70%] rounded-2xl px-6 py-3 ${
                                                msg.isUser
                                                    ? 'bg-blue-600 text-white ml-auto'
                                                    : 'bg-gray-700 text-gray-100'
                                            } shadow-lg`}
                                        >
                                            <p className="text-sm md:text-base">{msg.text}</p>
                                        </div>
                                    )}
                                </motion.div>
                            ))}
                            {error && (
                                <motion.div
                                    variants={messageVariants}
                                    initial="initial"
                                    animate="animate"
                                    exit="exit"
                                    className="text-red-500 text-center py-2"
                                >
                                    {error}
                                </motion.div>
                            )}
                            <div ref={messagesEndRef} />
                        </div>
                    </div>

                    <div className="p-4 border-t border-gray-700">
                        <form onSubmit={handleSendMessage} className="flex gap-2">
                            <input
                                type="text"
                                value={input}
                                onChange={(e) => setInput(e.target.value)}
                                placeholder={isLoading ? "Waiting for response..." : "Type your message..."}
                                disabled={isLoading}
                                className="flex-1 p-3 rounded-lg bg-gray-700 text-white placeholder-gray-400 border border-gray-600 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent disabled:opacity-50"
                            />
                            <motion.button
                                type="submit"
                                disabled={isLoading || !input.trim()}
                                whileHover={{ scale: 1.05 }}
                                whileTap={{ scale: 0.95 }}
                                className="p-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2 focus:ring-offset-gray-800 transition-colors duration-200 disabled:opacity-50 disabled:cursor-not-allowed"
                            >
                                <PaperAirplaneIcon className="w-6 h-6" />
                            </motion.button>
                        </form>
                    </div>
                </motion.div>
            </div>
        </LayoutGroup>
    );
};

export default Chat;
