import React, { useState, useEffect, useRef } from 'react';
import { motion, AnimatePresence, domMax, LazyMotion, MotionConfig } from 'framer-motion';
import { PaperAirplaneIcon, ChevronDownIcon } from '@heroicons/react/24/solid';

interface Character {
    id: number;
    name: string;
    movie: string;
}

interface Message {
    text: string;
    isUser: boolean;
    id: string;
}

const Chat: React.FC = () => {
    const [messages, setMessages] = useState<Message[]>([]);
    const [input, setInput] = useState<string>('');
    const [characters, setCharacters] = useState<Character[]>([]);
    const [selectedCharacter, setSelectedCharacter] = useState<Character | null>(null);
    const [isDropdownOpen, setIsDropdownOpen] = useState(false);
    const messagesEndRef = useRef<HTMLDivElement>(null);
    const dropdownRef = useRef<HTMLDivElement>(null);

    const scrollToBottom = () => {
        messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
    };

    const fetchCharacters = async () => {
        try {
            const response = await fetch('http://localhost:8000/characters');
            const data = await response.json();
            setCharacters(data);
        } catch (error) {
            console.error('Error fetching characters:', error);
        }
    };

    const sendMessage = async (e: React.FormEvent) => {
        e.preventDefault();
        if (input.trim()) {
            const userMessageId = Date.now().toString();
            setMessages(prev => [...prev, { text: input, isUser: true, id: userMessageId }]);

            try {
                const response = await fetch('http://localhost:8000/chat', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                    body: JSON.stringify({
                        message: input,
                        character_id: selectedCharacter?.id
                    }),
                });
                const data = await response.json();

                setMessages(prev => [...prev, { 
                    text: data.response, 
                    isUser: false, 
                    id: (Date.now() + 1).toString() 
                }]);
                setInput('');
            } catch (error) {
                console.error('Error sending message:', error);
            }
        }
    };

    useEffect(() => {
        fetchCharacters();

        // Close dropdown when clicking outside
        const handleClickOutside = (event: MouseEvent) => {
            if (dropdownRef.current && !dropdownRef.current.contains(event.target as Node)) {
                setIsDropdownOpen(false);
            }
        };

        document.addEventListener('mousedown', handleClickOutside);
        return () => document.removeEventListener('mousedown', handleClickOutside);
    }, []);

    useEffect(() => {
        scrollToBottom();
    }, [messages]);

    return (
        <MotionConfig reducedMotion="user">
            <LazyMotion features={domMax}>
                <div className="flex items-center justify-center min-h-screen bg-gray-900 p-4">
                    <motion.div 
                        initial={{ scale: 0.95, opacity: 0 }}
                        animate={{ scale: 1, opacity: 1 }}
                        className="w-full max-w-4xl rounded-xl bg-gray-800 shadow-2xl overflow-hidden"
                    >
                        <div className="p-4 border-b border-gray-700">
                            <div className="relative" ref={dropdownRef}>
                                <button
                                    onClick={() => setIsDropdownOpen(!isDropdownOpen)}
                                    className="w-full p-3 text-left bg-gray-700 hover:bg-gray-600 rounded-lg flex items-center justify-between text-white transition-colors duration-200"
                                >
                                    <span>{selectedCharacter ? `${selectedCharacter.name} from ${selectedCharacter.movie}` : 'Select a character'}</span>
                                    <ChevronDownIcon className={`w-5 h-5 transition-transform duration-200 ${isDropdownOpen ? 'transform rotate-180' : ''}`} />
                                </button>

                                <AnimatePresence mode="wait" presenceAffectsLayout>
                                    {isDropdownOpen && (
                                        <motion.div
                                            initial={{ opacity: 0, y: -10 }}
                                            animate={{ opacity: 1, y: 0 }}
                                            exit={{ opacity: 0, y: -10 }}
                                            className="absolute w-full mt-2 py-2 bg-gray-700 rounded-lg shadow-xl z-10"
                                        >
                                            {characters.map((character) => (
                                                <motion.button
                                                    key={character.id}
                                                    whileHover={{ backgroundColor: 'rgba(75, 85, 99, 1)' }}
                                                    onClick={() => {
                                                        setSelectedCharacter(character);
                                                        setIsDropdownOpen(false);
                                                    }}
                                                    className="w-full px-4 py-2 text-left text-white hover:bg-gray-600 transition-colors duration-200"
                                                >
                                                    {character.name} from {character.movie}
                                                </motion.button>
                                            ))}
                                        </motion.div>
                                    )}
                                </AnimatePresence>
                            </div>
                        </div>

                        <div className="h-[60vh] overflow-y-auto p-4 bg-gray-800">
                            <div className="space-y-4">
                                <AnimatePresence mode="wait" initial={false} presenceAffectsLayout>
                                    {messages.map((msg) => (
                                        <motion.div
                                            key={msg.id}
                                            initial={{ opacity: 0, y: 20, scale: 0.95 }}
                                            animate={{ opacity: 1, y: 0, scale: 1 }}
                                            exit={{ opacity: 0, scale: 0.95 }}
                                            transition={{ type: "spring", duration: 0.5 }}
                                            layout
                                            className={`flex ${msg.isUser ? 'justify-end' : 'justify-start'}`}
                                        >
                                            <div
                                                className={`max-w-[70%] rounded-2xl px-6 py-3 ${
                                                    msg.isUser
                                                        ? 'bg-blue-600 text-white ml-auto'
                                                        : 'bg-gray-700 text-gray-100'
                                                } shadow-lg`}
                                            >
                                                <p className="text-sm md:text-base">{msg.text}</p>
                                            </div>
                                        </motion.div>
                                    ))}
                                </AnimatePresence>
                                <div ref={messagesEndRef} />
                            </div>
                        </div>

                        <div className="p-4 border-t border-gray-700">
                            <form onSubmit={sendMessage} className="flex gap-2">
                                <input
                                    type="text"
                                    value={input}
                                    onChange={(e) => setInput(e.target.value)}
                                    placeholder="Type your message..."
                                    className="flex-1 p-3 rounded-lg bg-gray-700 text-white placeholder-gray-400 border border-gray-600 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                                />
                                <motion.button
                                    type="submit"
                                    whileHover={{ scale: 1.05 }}
                                    whileTap={{ scale: 0.95 }}
                                    className="p-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2 focus:ring-offset-gray-800 transition-colors duration-200"
                                >
                                    <PaperAirplaneIcon className="w-6 h-6" />
                                </motion.button>
                            </form>
                        </div>
                    </motion.div>
                </div>
            </LazyMotion>
        </MotionConfig>
    );
};

export default Chat;
